import React, { useState } from 'react';
import Header from "../components/header";
import Footer from "../components/footer";
import ceasul1 from '../SURSE/stockpoze/ceasul1.png';
import ceasul2 from '../SURSE/stockpoze/ceasul2.png';
import ceasul3 from '../SURSE/stockpoze/ceasul3.png';

function Produs() {
    // Definirea stărilor pentru imagini
    const [img1, setImg1] = useState(ceasul1);
    const [img2, setImg2] = useState(ceasul2);

    // Funcția pentru schimbarea imaginilor la galben
    const culoaregalbena = () => {
        setImg1(ceasul1); // Poate fi o altă imagine pentru galben
        setImg2(ceasul2);
    };

    // Funcția pentru schimbarea imaginilor la negru
    const culoareneagra = () => {
        setImg1(ceasul3); // Imaginea pentru varianta "neagră"
        setImg2(ceasul1); // Exemplu pentru imaginea secundară
    };

    return (
        <>
            <Header />
            <div className="bg-[rgba(202,212,212,0.75)] flex w-screen h-fit pt-[100px] pb-[100px]">
                <div className="w-1/2 h-full items-end flex flex-col right-0">
                    <img src={img1} alt="ceas" id="img1" className="mr-[85px]" height="250px" width="250px" />
                    <div>
                        <img src={img2} alt="ceas2" id="img2" className="h-[300px] w-[200px]" />
                        <img src={ceasul3} alt="ceas3" className="h-[300px] w-[200px] object-cover" />
                    </div>
                </div>
                <div className="ml-[20px] w-[40vw] flex flex-col justify-center">
                    <h2 className="text-[48px] underline cursor-default">Ceas GUESS</h2>
                    <small className="font-bold text-gray-500 text-[32px]">349,99 RON</small>
                    <br /><br />
                    <small className="text-medium">Un ceas Guess este un accesoriu de modă elegant și sofisticat, renumit pentru designul său modern și atrăgător.</small>
                    <h3>Descriere</h3>
                    <p>Acesta combină elemente de stil clasic cu tendințe contemporane, fiind adesea decorat cu detalii rafinate precum cristale și finisaje metalice de înaltă calitate.</p>
                    <h3>TRANSPORT</h3>
                    <p>Transport oriunde in ROMANIA!</p>

                    <div className="w-fit h-[50px]">
                        <button onClick={culoaregalbena}>Culoare Galbenă</button>
                        <button onClick={culoareneagra}>Culoare Neagră</button>
                    </div>

                    <div>
                        <button className="bg-[rgb(255,196,88)] text-[28px] font-[fantasy] rounded-[8px] px-[20px] py-[10px] shadow-[12px_12px_12px_black] border-[1.5px] cursor-pointer active:border-[wheat] active:scale-[1.05]">
                            BUY NOW
                        </button>
                    </div>
                </div>
            </div>
            <Footer />
        </>
    )
}

export default Produs;
